<?phpclass Feedback{    public function getInsert(){        global $db;        global $constructor;        global $parameter;        $dom = "";         $sql = "SELECT * FROM `hashtag_feedback` ORDER BY `timestamp` DESC;";        $result = $db->db_fetchall_array($sql, __LINE__, __FILE__);        $before = array();        array_push($before, rand(0, count($result)-1));        for ($i = 0; $i < 5; $i++){            $rand = rand(0, count($result)-1);                        for ($i = 0; $i < count($before); $i++){                if ($before[$i] === $rand){                    $i = 0;                    $rand = rand(0, count($result)-1);                }            }                        $from = "https://vk.com/id";                         switch ($result[$rand]["from"]) {                    case 1:                        $from = 'https://ok.ru/profile/';                    break;                    case 2:                        $from = 'https://www.facebook.com/';                    break;                }                            array_push($before, $rand);            $dom .=                '<div  class="gallery_element_box" id="first_gallery_box_div" style="height: 180.8px;">                    <div id="feedback_'.$rand.'" class="friend_block">                        <div class="frienddata_wrapper">                            <div class="friendphoto_wrapper">                                <img id="user_'.$rand.'_photo" class="friend_image_w" src="'.$result[$rand]["user_photo"].'" />                            </div>                            <span class="friendname"><a class="opacity_transition" target="_blank" href="'.$from.$result[$rand]["user_id"].'">'.$result[$rand]["user_name"].'</a></span>                            <div class="message_preview">'.$result[$rand]["text"].'</div>                        </div>                    </div>                </div>';        }        return $dom;    }        public function get($page_value){        global $db;        global $constructor;        global $parameter;        $dom = ""; // у рио делает тоггл на класс fullViewShowed если развернуть категории        $sql = "SELECT * FROM `hashtag_feedback` ORDER BY `timestamp` DESC;";        $result = $db->db_fetchall_array($sql, __LINE__, __FILE__);        $page = $page_value-1;        $per = $parameter->pagination_pages;        $pages = ceil(count($result)/3);        for ($i = $page*$per; $i < $page*$per+3; $i++){            $out = 0;            $from = "https://vk.com/id";            if ($i < count($result)){                switch ($result[$i]["from"]) {                    case 1:                        $from = 'https://ok.ru/profile/';                    break;                    case 2:                        $from = 'https://www.facebook.com/';                    break;                }                                $dom .=                 '<div id="feedback_'.$i.'" class="friend_block">                        <div class="frienddata_wrapper"><div class="friendphoto_wrapper">                            <img id="user_'.$i.'_photo" src="'.$result[$i]["user_photo"].'" class="friend_image_w">                        </div>                        <span class="friendname"><a target="_blank" class="opacity_transition" href="'.$from.$result[$i]["user_id"].'">'.$result[$i]["user_name"].'</a></span>                        <br><div class="message_preview">'.$result[$i]["text"].'</div>                        </div>                        <span class="msg_time">'.$date = date('d.m', $result[$i]["timestamp"]).'</span>                </div>';            }       }       $dom .= '<div style="text-align:center"><ul class="pagination">'.$constructor->setPagination($page_value, $pages, null).'</ul></div>';       return $dom;    }    public function set($user_id, $user_name, $user_photo, $text, $from){        global $db;        $sql = sprintf("INSERT INTO `hashtag_feedback` (`user_id`, `user_name`, `user_photo`, `text`, `timestamp`, `from`) VALUES (%d, '%s', '%s', '%s', %d, %d);", // добавляем транзкцию пополнения юзеру            mysql_real_escape_string($user_id),            mysql_real_escape_string($user_name),            mysql_real_escape_string($user_photo),            mysql_real_escape_string($text),            mysql_real_escape_string(time()),            mysql_real_escape_string($from));        return $db->db_query($sql, __LINE__, __FILE__);    }    public function okAuth($token){        return $sig = md5($token."82630551AD94447BFE21487C");    }}?>